function ProgressBar({ timer, focusInterval, breakInterval, mode }) {
  return (
    <div className="row mb-2">
      <div className="col">
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={mode ? (1 - timer / (focusInterval * 60)) * 100 : (1 - timer / (breakInterval * 60))} // TODO: Increase aria-valuenow as elapsed time increases
            style={mode ? { width: `${(1 - timer / (focusInterval * 60)) * 100}%` } : { width: `${(1 - timer / (breakInterval * 60)) * 100}%` } } // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  );
}

export default ProgressBar;
