import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import { secondsToDuration } from "../utils/duration";
import { minutesToDuration } from "../utils/duration"
function Timer({
  focusInterval,
  breakInterval,
  isTimerRunning,
  timer,
  setTimer,
  mode,
  setMode
}) {


useInterval(
    () => {
      if (timer === 0) { 
      new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
        //play the alarm
       if (mode) {
          setMode((prevState) => !prevState);
          //change status from Focus to break or vise-versa
          return setTimer((breakInterval) * 60)          
        }
        else {
        setMode((prevState) => !prevState);
        //change status from Focus to break or vise-versa
        return setTimer((focusInterval) * 60)     
        }
      }
      return setTimer(timer - 1);
    },
    isTimerRunning ? 1000 : null
  );


  return (
    <div className="row mb-2">
      <div className="col">
        {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
        <h2 data-testid="session-title">
          {mode ? "Focusing" : "Break"} for {mode ? minutesToDuration(focusInterval) : minutesToDuration(breakInterval)} minutes
        </h2>
        {/* TODO: Update message below to include time remaining in the current session */}
        <p className="lead" data-testid="session-sub-title">
          {secondsToDuration(timer)} remaining
        </p>
      </div>
    </div>
  );
}

export default Timer;
